import { Component, Input, ViewEncapsulation } from "@angular/core";
import {
  CrtInterfaceDesignerItem,
  CrtViewElement,
} from "@creatio-devkit/common";

@CrtViewElement({
  selector: "qnovate-demo",
  type: "qnovate.Demo",
})
@CrtInterfaceDesignerItem({
  toolbarConfig: {
    caption: "Your component",
    name: "qnovate-demo",
    icon: require("!!raw-loader?{esModule:false}!./demo-icon.svg"),
  },
})
@Component({
  selector: "qnovate-radio-button",
  template: `
    <div class="radio-group">
      <label *ngFor="let option of options">
        <input 
          type="radio" 
          [name]="groupName" 
          [value]="option.value"
          [checked]="option.value === selectedValue"
          (change)="onSelectionChange(option.value)">
        {{option.label}}
      </label>
    </div>
  `,
  styles: [`
    .radio-group {
      display: flex;
      flex-direction: column;
      gap: 8px;
    }
    label {
      display: flex;
      align-items: center;
      gap: 8px;
      cursor: pointer;
    }
    input[type="radio"] {
      margin: 0;
      cursor: pointer;
    }
  `],
  encapsulation: ViewEncapsulation.ShadowDom
})
export class DemoComponent {
  @Input() options: Array<{label: string, value: any}> = [
    { label: 'Option 1', value: '1' },
    { label: 'Option 2', value: '2' },
    { label: 'Option 3', value: '3' }
  ];

  @Input() groupName: string = 'radioGroup';
  @Input() selectedValue: any = '1';

  onSelectionChange(value: any) {
    this.selectedValue = value;
  }
}
